import{headerApp}from"./main/header.js";import{formApp}from"./modules/form.js";import"./modules/slider.js";headerApp.allMethods(),formApp.allMethods();const headerApp={fixedHeader(){const t=document.getElementsByClassName("header")[0];200<document.documentElement.scrollTop&&t.classList.add("fixed"),document.body.onscroll=e=>{e=e.target.scrollingElement;100<=e.scrollTop?t.classList.add("fixed"):t.classList.remove("fixed")}},addActiveClassToAnchor(){const e=document.querySelectorAll(".menu__item_link");e.forEach(e=>{e.addEventListener("click",e=>{const t=document.querySelectorAll(".active"),s=document.querySelectorAll(`[href='${e.target.hash}']`);t.forEach(e=>{e.classList.remove("active")}),s.forEach(e=>{e.classList.add("active")})})})},allMethods(){this.fixedHeader(),this.addActiveClassToAnchor()}},formApp={submit(){const e=document.getElementById("form"),t=document.getElementById("overlay"),s=document.getElementById("closeModal");e.addEventListener("submit",e=>{e.preventDefault(),e.target.reset(),$("#js-select").val(null).trigger("change"),t.classList.add("show")}),s.addEventListener("click",e=>{t.classList.remove("show")})},maskInput(){$("#phone").inputmask({mask:["+380 (99) 999-99-99"],jitMasking:3,showMaskOnHover:!1,autoUnmask:!0})},initSelect2(){$("#js-select").select2({placeholder:"Оберіть пункт"})},allMethods(){this.submit(),this.maskInput(),this.initSelect2()}};$("#owl-demo").owlCarousel({navigation:!0,items:1,loop:!0,nav:!0});export{headerApp,formApp};